, MANAGER_ID      NUMBER(6)    CONSTRAINT EMP4_MANAGER_FK REFERENCES EMPLOYEES4(EMPLOYEE_ID)
, DEPARTMENT_ID   NUMBER(4)    CONSTRAINT EMP4_DEPT_FK REFERENCES DEPARTMENTS4(DEPARTMENT_ID)
);


